(function() {
    'use strict';
    
    // Simple icon mapping for basic icons
    const iconMap = {
        'fa-file-pdf': '📄',
        'fa-github': '�', // Changed from octopus to generic link
        'fa-arxiv': '📖',
        'fa-pause': '⏸',
        'fa-play': '▶',
        'fa-redo': '🔄'
    };

    // Replace icon classes with actual icons on page load
    document.addEventListener('DOMContentLoaded', function() {
        Object.keys(iconMap).forEach(iconClass => {
            const elements = document.querySelectorAll(`.${iconClass}`);
            elements.forEach(el => {
                if (!el.textContent.trim()) {
                    el.textContent = iconMap[iconClass];
                }
            });
        });
    });

})();